const PlatformList = () => {
  const [searchTerm, setSearchTerm] = useState("");
  const [currentPage, setCurrentPage] = useState(1);
  const [isSliding, setIsSliding] = useState(false);
  const [slideDirection, setSlideDirection] = useState(null);
  const slideRef = useRef(null);

  // Track page view when component mounts
  useEffect(() => {
    ReactGA.send({ hitType: "pageview", page: "/platform" });
  }, []);

  // Track search interactions
  const handleSearch = (e) => {
    const term = e.target.value;
    setSearchTerm(term);
    
    // Track search event
    if (term.length > 0) {
      ReactGA.event({
        category: "Search",
        action: "Platform Search",
        label: term
      });
    }
  };

  // Track platform clicks
  const handlePlatformClick = (platformName) => {
    ReactGA.event({
      category: "Platform",
      action: "Platform Click",
      label: platformName
    });
  };

  // Track pagination
  const handlePageChange = (pageNumber) => {
    if (pageNumber < 1 || pageNumber > totalPages || pageNumber === currentPage) return;
    
    ReactGA.event({
      category: "Navigation",
      action: "Page Change",
      label: `Page ${pageNumber}`
    });

    goToPage(pageNumber);
  };

  const filteredPlatforms = coursePlatforms.filter((platform) =>
    platform.name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  const totalPages = Math.ceil(filteredPlatforms.length / ITEMS_PER_PAGE);

  const startIndex = (currentPage - 1) * ITEMS_PER_PAGE;
  const currentItems = filteredPlatforms.slice(startIndex, startIndex + ITEMS_PER_PAGE);

  // Reset page on search term change
  useEffect(() => {
    setCurrentPage(1);
  }, [searchTerm]);

  const goToPage = (pageNumber) => {
    if (pageNumber < 1 || pageNumber > totalPages || pageNumber === currentPage) return;

    // Determine slide direction
    setSlideDirection(pageNumber > currentPage ? "left" : "right");
    setIsSliding(true);

    // Wait for slide out animation to finish before changing content
    setTimeout(() => {
      setCurrentPage(pageNumber);
      setSlideDirection(pageNumber > currentPage ? "right" : "left"); // slide in from opposite direction
    }, 500);

    // After sliding in reset sliding state
    setTimeout(() => {
      setIsSliding(false);
      setSlideDirection(null);
    }, 1000);
  };

  // Calculate transform for sliding
  let transformStyle = "translateX(0)";
  if (isSliding && slideDirection === "left") transformStyle = "translateX(-100%)";
  else if (isSliding && slideDirection === "right") transformStyle = "translateX(100%)";

  return (
    <section id="platforms" className="platforms-section">
      <div className="container">
        <h2 className="section-title">Explore Course Platforms</h2>

        <div className="search-bar-wrapper">
          <input
            type="text"
            placeholder="Search platforms..."
            value={searchTerm}
            onChange={handleSearch}
            className="search-bar"
          />
          <svg
            xmlns="http://www.w3.org/2000/svg"
            className="search-icon"
            fill="none"
            viewBox="0 0 24 24"
            stroke="currentColor"
          >
            <path
              strokeLinecap="round"
              strokeLinejoin="round"
              strokeWidth={2}
              d="M21 21l-4.35-4.35M10 18a8 8 0 100-16 8 8 0 000 16z"
            />
          </svg>
        </div>

        <div className="platforms-grid">
          <div
            className="platforms-slide"
            style={{ transform: transformStyle }}
            ref={slideRef}
          >
            {currentItems.length > 0 ? (
              currentItems.map((platform) => {
                const Icon = platform.Icon;
                return (
                  <div key={platform.id} className="platform-card">
                    {Icon ? (
                      <Icon size={48} className="platform-logo" />
                    ) : platform.image ? (
                      <img
                        src={platform.image}
                        alt={platform.name}
                        className="platform-logo"
                        style={{ width: 50, height: 50 }}
                      />
                    ) : (
                      <div className="platform-logo-placeholder" />
                    )}
                    <h3>{platform.name}</h3>
                    <p>{platform.description}</p>
                    <a
                      href={platform.link}
                      target="_blank"
                      rel="noopener noreferrer"
                      className="visit-btn"
                      onClick={() => handlePlatformClick(platform.name)}
                    >
                      Visit Platform
                    </a>
                  </div>
                );
              })
            ) : (
              <p>No platforms found.</p>
            )}
          </div>
        </div>

        {totalPages > 1 && (
          <div className="pagination">
            <button 
              onClick={() => handlePageChange(currentPage - 1)} 
              disabled={currentPage === 1}
            >
              Prev
            </button>
            {[...Array(totalPages)].map((_, i) => (
              <button
                key={i + 1}
                className={`page-btn ${currentPage === i + 1 ? "active" : ""}`}
                onClick={() => handlePageChange(i + 1)}
              >
                {i + 1}
              </button>
            ))}
            <button 
              onClick={() => handlePageChange(currentPage + 1)} 
              disabled={currentPage === totalPages}
            >
              Next
            </button>
          </div>
        )}
      </div>
    </section>
  );
}

export default PlatformList; // ✅ default export

